import * as types from "./types";
import { get, post } from "./fetchMethods";

export const adminLogin = loginRequest => dispatch => {
  const options = {

    body: JSON.stringify(loginRequest)
  };

  post('/login', options)
    .then(response => {
      dispatch({
        type: types.LOGIN,
        payload: response
      });
    })
    .catch(err => {
      console.error("Request failed", err);
    });
};

export const search = (searchRequestString, from = "user") => dispatch => {
  if (searchRequestString === null) {
    if (from === "care_giver") {
      dispatch({
        type: types.CAREGIVER_SEARCH_LIST,
        payload: {}
      });
    } else if (from === "care_manager") {
      dispatch({
        type: types.CAREMANAGER_SEARCH_LIST,
        payload: {}
      });
    } else if (from === "guardian") {
      dispatch({
        type: types.GAURDIAN_SEARCH_LIST,
        payload: {}
      });
    } else if (from === "physical_therapist") {
      dispatch({
        type: types.PHYSICAL_THERAPIST_SEARCH_LIST,
        payload: {}
      });
    } else if (from === "nurse") {
      dispatch({
        type: types.NURSE_SEARCH_LIST,
        payload: {}
      });
    } else {
      dispatch({
        type: types.CLIENT_SEARCH_LIST,
        payload: {}
      });
    }
  } else {
    let url = `${process.env.REACT_APP_API_BASE_URL}/user/search/list?type=${from}`;

    if (searchRequestString && searchRequestString !== "") {
      url += searchRequestString;
    }

    get(url)
      .then(response => {
        if (from === "care_giver") {
          dispatch({
            type: types.CAREGIVER_SEARCH_LIST,
            payload: response
          });
        } else if (from === "care_manager") {
          dispatch({
            type: types.CAREMANAGER_SEARCH_LIST,
            payload: response
          });
        } else if (from === "guardian") {
          dispatch({
            type: types.GAURDIAN_SEARCH_LIST,
            payload: response
          });
        } else if (from === "physical_therapist") {
          dispatch({
            type: types.PHYSICAL_THERAPIST_SEARCH_LIST,
            payload: response
          });
        } else if (from === "nurse") {
          dispatch({
            type: types.NURSE_SEARCH_LIST,
            payload: response
          });
        } else {
          dispatch({
            type: types.CLIENT_SEARCH_LIST,
            payload: response
          });
        }
      })
      .catch(err => {
        console.error("Request failed", err);
      });
  }
};

export const getCareGivers = () => dispatch => {
  let url = `${process.env.REACT_APP_API_BASE_URL}/caregiver`;

  get(url)
    .then(response =>
      dispatch({
        type: types.CARE_GIVER_LIST,
        payload: response
      })
    )
    .catch(err => {
      console.error("Request failed", err);
    });
};
